export function SideBarJs(){document.addEventListener("DOMContentLoaded",()=>{let e=document.querySelector(".sidebar"),t=document.querySelector("#btn"),n=document.querySelector(".bx-search");function s(){e&&(e.classList.contains("open")?t.classList.replace("bx-menu","bx-menu-alt-right"):t.classList.replace("bx-menu-alt-right","bx-menu"))}t&&t.addEventListener("click",()=>{e.classList.toggle("open"),s()}),n&&n.addEventListener("click",()=>{e.classList.toggle("open"),s()})})}